package sec04;

public class Print01 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String myName = "박병준";
		int myAge = 25;
		
		System.out.println("이름 : " + myName);
		System.out.println("나이 : " + myAge);
		
		System.out.print("\n");
		System.out.print("이름 : " + myName);
		System.out.print("  나이 : " + myAge);
	}

}
