package sec04;

public class TypeConversion02 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x = 10;
		double y = 3.0;
		
		System.out.println(x + y);
		System.out.println(x - y);
		System.out.println(x * y);
		System.out.println(x / y);
		
	}

}
